import UIKit

class BASACardLimitsRouter: BASACardLimitsWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = BASACardLimitsViewController(nibName: nil, bundle: Bundle.init(for: BASACardLimitsRouter.self))
        let interactor = BASACardLimitsInteractor()
        let router = BASACardLimitsRouter()
        let presenter = BASACardLimitsPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
