import UIKit

class GSSACardPaymentBtnCoreViewPresenter: GSSACardPaymentBtnCoreViewPresenterProtocol {

    weak private var view: GSSACardPaymentBtnCoreViewViewProtocol?
    var interactor: GSSACardPaymentBtnCoreViewInteractorProtocol?
    private let router: GSSACardPaymentBtnCoreViewWireframeProtocol

    init(interface: GSSACardPaymentBtnCoreViewViewProtocol, interactor: GSSACardPaymentBtnCoreViewInteractorProtocol?, router: GSSACardPaymentBtnCoreViewWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    
    
    func registerNotifications(){
        NotificationCenter.default.addObserver(self, selector: #selector(abortPayment(_:)), name: NSNotification.Name.init("abortPaymentNotification"), object: nil)
    }

    
    
    
    @objc func abortPayment(_ notification: Notification) {
        self.view?.abortPayment()
    }
    
}
