import UIKit

class GSSARequestDebitCardRouter: GSSARequestDebitCardWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = GSSARequestDebitCardViewController(nibName: nil, bundle: Bundle.init(for: GSSARequestDebitCardRouter.self))
        let interactor = GSSARequestDebitCardInteractor()
        let router = GSSARequestDebitCardRouter()
        let presenter = GSSARequestDebitCardPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
