import UIKit

class GSSACardScannerRouter: GSSACardScannerWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = GSSACardScannerViewController(nibName: nil, bundle: Bundle.init(for: GSSACardScannerRouter.self))
        let interactor = GSSACardScannerInteractor()
        let router = GSSACardScannerRouter()
        let presenter = GSSACardScannerPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
