import UIKit

class BASADigitalCardPresenter: BASADigitalCardPresenterProtocol {

    weak private var view: BASADigitalCardViewProtocol?
    var interactor: BASADigitalCardInteractorProtocol?
    private let router: BASADigitalCardWireframeProtocol

    init(interface: BASADigitalCardViewProtocol, interactor: BASADigitalCardInteractorProtocol?, router: BASADigitalCardWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func makeDigitalDataRequest(Body: Transaction, DataCard: @escaping (DigitalCardResponse?) -> ()) {
        interactor?.TryGetCardDigitalCardData(Body: Body, DataCard: DataCard)
    }
}
