import UIKit

open class BASADigitalCardRouter: BASADigitalCardWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    public static func createModule(userBalance: String) -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = BASADigitalCardViewController(nibName: "BASADigitalCardViewController", bundle: Bundle(for: BASADigitalCardRouter.self))
        view.modalPresentationStyle = .overCurrentContext
        let interactor = BASADigitalCardInteractor()
        let router = BASADigitalCardRouter()
        let presenter = BASADigitalCardPresenter(interface: view, interactor: interactor, router: router)
        
        view.userBalance = userBalance
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
