import UIKit
import GSSAVisualComponents

class GSSAFundSetCardNumberViewController: UIViewController, GSSAFundSetCardNumberViewProtocol {

    @IBOutlet weak var buttonView       : UIView!
    @IBOutlet weak var buttonViewText   : GSVCLabel!
    
	var presenter: GSSAFundSetCardNumberPresenterProtocol?
    var buttonTitle = "Te sugerimos leer el Aviso de Privacidad."
    var myMutableString = NSMutableAttributedString()
    
	override func viewDidLoad() {
        super.viewDidLoad()
        myMutableString = NSMutableAttributedString(string: buttonTitle)
        myMutableString.addAttribute(NSAttributedString.Key.foregroundColor, value: UIColor.systemGreen, range: NSRange(location:20,length:21))
        buttonViewText.attributedText = myMutableString
        buttonView.backgroundColor = UIColor.GSVCBase300()
        self.navigationController?.setNavigationBarHidden(true, animated: false)
    }
    
    override func viewWillAppear(_ animated: Bool) {
        self.navigationController?.setNavigationBarHidden(true, animated: false)
    }
    
    @IBAction func next(_ sender: Any) {
        let view = GSSAFundAddCardRouter.createModule()
        self.navigationController?.pushViewController(view, animated: true)
    }
    
    @IBAction func close(_ sender: Any){
        self.navigationController?.popViewController(animated: true)
    }
}
